import React, { useEffect, useState } from "react";
import API from "../api.ts";
import { Pie, Line, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  LineElement,
  PointElement,
  LinearScale,
  CategoryScale,
  Tooltip,
  Legend,
  BarElement,
} from "chart.js";

ChartJS.register(
  ArcElement,
  LineElement,
  PointElement,
  LinearScale,
  CategoryScale,
  Tooltip,
  Legend,
  BarElement
);

interface PaymentModeData {
  paymentMode: string;
  total: number;
}

interface Trend {
  date: string;
  total: number;
}

interface MonthlyTotal {
  month: string;
  total: number;
}

export default function Analytics() {
  const month = new Date().toISOString().slice(0, 7);
  const [modes, setModes] = useState<PaymentModeData[]>([]);
  const [trend, setTrend] = useState<Trend[]>([]);
  const [monthlyTotals, setMonthlyTotals] = useState<MonthlyTotal[]>([]);

  useEffect(() => {
    API.get("/expenses/paymentModes", { params: { month } })
      .then((r) => setModes(r.data))
      .catch(() => setModes([]));

    API.get("/expenses/trend", { params: { month } })
      .then((r) => setTrend(r.data))
      .catch(() => setTrend([]));

    API.get("/expenses/monthly-totals")
      .then((r) => setMonthlyTotals(r.data))
      .catch(() => setMonthlyTotals([]));
  }, [month]);

  if (modes.length === 0 && trend.length === 0 && monthlyTotals.length === 0) {
    return (
      <div className="box">
        <p className="no-data">No analytics data available.</p>
      </div>
    );
  }

  const pieData = {
    labels: modes.map((m) => m.paymentMode),
    datasets: [
      {
        data: modes.map((m) => m.total),
        backgroundColor: ["#4CAF50", "#FF9800", "#03A9F4"],
      },
    ],
  };

  const lineData = {
    labels: trend.map((t) => t.date),
    datasets: [
      {
        label: "Daily Spend",
        data: trend.map((t) => t.total),
        borderColor: "#36a2eb",
        fill: false,
        tension: 0.3,
      },
    ],
  };

  const barData = {
    labels: monthlyTotals.map((m) => m.month),
    datasets: [
      {
        label: "Monthly Spend",
        data: monthlyTotals.map((m) => m.total),
        backgroundColor: "#673ab7",
      },
    ],
  };

  return (
    <div className="box">
      <h3>Analytics</h3>
      <div className="chart-row">
        <div className="chart-box">
          <h4 style={{ textAlign: "center" }}>Spending by Payment Mode</h4>
          <Pie data={pieData} />
        </div>
        <div className="chart-box">
          <h4 style={{ textAlign: "center" }}>Daily Spending Trend</h4>
          <Line data={lineData} />
        </div>
        <div className="chart-box">
          <h4 style={{ textAlign: "center" }}>Monthly Spending Overview</h4>
          <Bar data={barData} />
        </div>
      </div>
    </div>
  );
}
