import React, { useState } from "react";
import API from "../api.ts";

export default function AddExpense() {
  const [amount, setAmount] = useState("");
  const [desc, setDesc] = useState("");

  const handleAdd = () => {
    if (!amount || !desc) return alert("Enter details");
    API.post("/expenses", { amount: parseFloat(amount), description: desc, date: new Date().toISOString() })
      .then(() => alert("Added"))
      .catch(() => alert("Failed"));
    setAmount(""); setDesc("");
  };

  return (
    <div className="box">
      <h3>Add Expense</h3>
      <input className="input" type="number" placeholder="Amount" value={amount} onChange={e => setAmount(e.target.value)} />
      <input className="input" type="text" placeholder="Description" value={desc} onChange={e => setDesc(e.target.value)} />
      <button className="btn" onClick={handleAdd}>Add</button>
    </div>
  );
}
