import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import API from "../api.ts";
import "./Dashboard.css"; // Make sure this CSS file exists

export default function Dashboard() {
  const month = new Date().toISOString().slice(0, 7);
  const [spent, setSpent] = useState(0);
  const [budget, setBudget] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    API.get("/expenses/monthly", { params: { month } })
      .then((r) => setSpent(r.data.total))
      .catch(() => setSpent(0));

    API.get("/budget", { params: { month } })
      .then((r) => setBudget(r.data?.limit?.toString() || ""))
      .catch(() => setBudget(""));
  }, [month]);

  const saveBudget = () => {
    if (!budget || isNaN(parseFloat(budget))) return alert("Please enter a valid budget");
    API.post("/budget", { month, limit: parseFloat(budget) })
      .then(() => alert("Budget saved"))
      .catch(() => alert("Failed to save budget"));
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const percent = budget ? Math.min((spent / parseFloat(budget)) * 100, 100) : 0;

  return (
    <div className="dashboard-container">
      <div className="dashboard-box">
        <div className="dashboard-header">
          <h2 className="dashboard-title">Dashboard</h2>
          <button className="logout-button" onClick={handleLogout}>
            Logout
          </button>
        </div>

        <div className="budget-info">
          <p className="spent-text">Spent: ₹{spent.toFixed(2)}</p>
          <div className="budget-input-group">
            <input
              type="number"
              className="budget-input"
              value={budget}
              onChange={(e) => setBudget(e.target.value)}
              placeholder="Enter Monthly Budget"
            />
            <button className="save-button" onClick={saveBudget}>
              Save
            </button>
          </div>
        </div>

        <div className="progress-bar">
          <div
            className={`progress-fill ${percent < 100 ? "progress-green" : "progress-red"}`}
            style={{ width: `${percent}%` }}
          ></div>
        </div>

        <p className="progress-text">
          You’ve used {percent.toFixed(1)}% of your monthly budget.
        </p>
      </div>
    </div>
  );
}
