import React, { useEffect, useState } from "react";
import API from "../api.ts";
import { Pie, Line, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  LineElement,
  PointElement,
  LinearScale,
  CategoryScale,
  Tooltip,
  Legend,
  BarElement,
} from "chart.js";

ChartJS.register(
  ArcElement,
  LineElement,
  PointElement,
  LinearScale,
  CategoryScale,
  Tooltip,
  Legend,
  BarElement
);

interface PaymentModeData {
  paymentMode: string;
  total: number;
}

interface MonthlyTotal {
  month: string;
  total: number;
}

export default function Analytics() {
  const [month, setMonth] = useState(new Date().toISOString().slice(0, 7));
  const [modes, setModes] = useState<PaymentModeData[]>([]);
  const [monthlyTotals, setMonthlyTotals] = useState<MonthlyTotal[]>([]);

  useEffect(() => {
    // Pie chart data for selected month
    API.get("/expenses/paymentModes", { params: { month } })
      .then((r) => setModes(r.data))
      .catch(() => setModes([]));

    // Monthly totals for all months
    API.get("/expenses/monthly-totals")
      .then((r) => setMonthlyTotals(r.data))
      .catch(() => setMonthlyTotals([]));
  }, [month]);

  const pieData = {
    labels: modes.map((m) => m.paymentMode),
    datasets: [
      {
        data: modes.map((m) => m.total),
        backgroundColor: ["#4CAF50", "#FF9800", "#03A9F4", "#E91E63", "#9C27B0"],
      },
    ],
  };

  const monthlyTrendData = {
    labels: monthlyTotals.map((m) => m.month),
    datasets: [
      {
        label: "Monthly Spend",
        data: monthlyTotals.map((m) => m.total),
        borderColor: "#36a2eb",
        backgroundColor: "rgba(54, 162, 235, 0.2)",
        fill: true,
        tension: 0.3,
      },
    ],
  };

  const barData = {
    labels: monthlyTotals.map((m) => m.month),
    datasets: [
      {
        label: "Monthly Spend",
        data: monthlyTotals.map((m) => m.total),
        backgroundColor: "#673ab7",
      },
    ],
  };

  if (modes.length === 0 && monthlyTotals.length === 0) {
    return (
      <div className="box">
        <p className="no-data">No analytics data available.</p>
      </div>
    );
  }

  return (
    <div className="box">
      <h3>Analytics</h3>

      {/* Month Picker */}
      <div style={{ textAlign: "center", marginBottom: "1rem" }}>
        <label>
          <strong>Select Month:</strong>{" "}
          <input
            type="month"
            value={month}
            onChange={(e) => setMonth(e.target.value)}
            className="input"
            style={{ maxWidth: "200px" }}
          />
        </label>
      </div>

      <div className="chart-row">
        <div className="chart-box">
          <h4 style={{ textAlign: "center" }}>Spending by Payment Mode</h4>
          <Pie data={pieData} />
        </div>
        <div className="chart-box">
          <h4 style={{ textAlign: "center" }}>Monthly Spending Trend</h4>
          <Line data={monthlyTrendData} />
        </div>
        <div className="chart-box">
          <h4 style={{ textAlign: "center" }}>Monthly Spending Overview</h4>
          <Bar data={barData} />
        </div>
      </div>
    </div>
  );
}
