import React, { useEffect, useState } from "react";
import API from "../api.ts";
import { Pie, Line } from "react-chartjs-2";
import { Chart as ChartJS, ArcElement, LineElement, PointElement, LinearScale, CategoryScale, Tooltip, Legend } from "chart.js";
ChartJS.register(ArcElement, LineElement, PointElement, LinearScale, CategoryScale, Tooltip, Legend);

interface CatData { category: string; total: number }
interface Trend { date: string; total: number }

export default function Analytics() {
  const month = new Date().toISOString().slice(0,7);
  const [cats, setCats] = useState<CatData[]>([]);
  const [trend, setTrend] = useState<Trend[]>([]);

  useEffect(() => {
    API.get("/expenses/categories",{params:{month}}).then(r=>setCats(r.data)).catch(()=>setCats([]));
    API.get("/expenses/trend",{params:{month}}).then(r=>setTrend(r.data)).catch(()=>setTrend([]));
  },[month]);

  if (cats.length===0 && trend.length===0) return <div className="box"><p className="no-data">No analytics data available.</p></div>;

  const pieData = { labels: cats.map(c=>c.category), datasets: [{ data: cats.map(c=>c.total), backgroundColor:['#ff6384','#36a2eb','#ffce56'] }] };
  const lineData = { labels: trend.map(t=>t.date), datasets: [{ label:'Daily Spend', data: trend.map(t=>t.total), borderColor:'#36a2eb', fill:false }] };

  return (
    <div className="box">
      <h3>Analytics</h3>
      <div className="chart-row">
        <div className="chart-box"><Pie data={pieData} /></div>
        <div className="chart-box"><Line data={lineData} /></div>
      </div>
    </div>
  );
}
